// Parse the JSON string from the AI output
const aiOutput = $input.first().json.output;

try {
  // Clean the output - remove any markdown formatting if present
  let cleanedOutput = aiOutput;

  // Remove ```json from the beginning if it exists
  if (cleanedOutput.includes('```json')) {
    cleanedOutput = cleanedOutput.replace(/```json\s*/g, '');
  }

  // Remove ``` from the end if it exists
  if (cleanedOutput.includes('```')) {
    cleanedOutput = cleanedOutput.replace(/```/g, '');
  }

  // Trim whitespace
  cleanedOutput = cleanedOutput.trim();

  // Parse the JSON
  const workflowJson = JSON.parse(cleanedOutput);

  // Ensure required fields exist
  if (!workflowJson.name) {
    workflowJson.name = "Generated Workflow";
  }

  if (!workflowJson.nodes || !Array.isArray(workflowJson.nodes)) {
    throw new Error('Invalid workflow structure: missing nodes array');
  }

  if (!workflowJson.connections) {
    workflowJson.connections = {};
  }

  if (!workflowJson.settings) {
    workflowJson.settings = {
      "executionOrder": "v1",
      "saveManualExecutions": true,
      "saveDataSuccessExecution": "all",
      "saveExecutionProgress": true,
      "saveDataErrorExecution": "all",
      "timezone": "America/New_York"
    };
  }

  if (!workflowJson.staticData) {
    workflowJson.staticData = null;
  }

  // Return the parsed and validated JSON
  return {
    workflowObject: workflowJson,
    rawOutput: aiOutput
  };

} catch (error) {
  // If parsing fails, return error information
  return {
    error: error.message,
    rawOutput: aiOutput,
    hint: "The AI output could not be parsed as valid JSON. Check the raw output for syntax errors."
  };
}
