options.customCss:
:root {
  /* Font */
  --font-family: 'Inter', 'Open Sans', Arial, sans-serif;
  --font-weight-normal: 400;
  --font-weight-bold: 600;
  --font-size-body: 14px;
  --font-size-label: 15px;
  --font-size-input: 15px;
  --font-size-header: 24px;
  --font-size-subheader: 17px;
  --font-size-paragraph: 14px;
  --font-size-link: 14px;
  --font-size-error: 13px;
  --font-size-html-h1: 32px;
  --font-size-html-h2: 24px;
  --font-size-html-h3: 20px;
  --font-size-html-h4: 17px;
  --font-size-html-h5: 15px;
  --font-size-html-h6: 13px;

  /* Minimalist grayscale palette */
  --color-background: #fff;
  --color-card-bg: #fff;
  --color-card-border: #eaeaea;
  --color-card-shadow: rgba(0,0,0,0.02);
  --color-link: #111;
  --color-header: #111;
  --color-label: #1a1a1a;
  --color-input-border: #d0d0d0;
  --color-input-text: #222;
  --color-focus-border: #111;
  --color-submit-btn-bg: #111;
  --color-submit-btn-text: #fff;
  --color-error: #111;
  --color-required: #111;
  --color-clear-button-bg: #fff;
  --color-clear-button-border: #d0d0d0;
  --color-html-text: #1a1a1a;
  --color-html-link: #111;
  --color-header-subtext: #757575;
  --color-placeholder: #b0b0b0;

  /* Border radii */
  --border-radius-card: 14px;
  --border-radius-input: 10px;
  --border-radius-clear-btn: 50%;

  /* Spacing and Layout */
  --padding-container-top: 32px;
  --padding-card: 32px;
  --padding-form-input: 15px;
  --margin-bottom-card: 22px;
  --card-padding: 32px;
  --card-margin-bottom: 22px;

  /* Sizing */
  --container-width: 420px;
  --submit-btn-height: 46px;
  --checkbox-size: 19px;

  /* Shadows and effects */
  --box-shadow-card: 0 2px 12px 0 var(--color-card-shadow);
  --opacity-placeholder: 0.6;
}
/* Example of sleek minimalist form elements: */
input, select, textarea {
  font-family: var(--font-family);
  background: #fff;
  color: var(--color-input-text);
  border: 1.5px solid var(--color-input-border);
  border-radius: var(--border-radius-input);
  padding: var(--padding-form-input);
  transition: border 0.2s;
}

input:focus, select:focus, textarea:focus {
  border: 1.5px solid var(--color-focus-border);
  outline: none;
}

button[type="submit"] {
  background: var(--color-submit-btn-bg);
  color: var(--color-submit-btn-text);
  border: none;
  border-radius: var(--border-radius-input);
  height: var(--submit-btn-height);
  font-weight: var(--font-weight-bold);
  letter-spacing: 0.01em;
  cursor: pointer;
  transition: background 0.2s;
}

button[type="submit"]:hover {
  background: #333;
}

label {
  color: var(--color-label);
  font-size: var(--font-size-label);
  font-weight: var(--font-weight-bold);
  margin-bottom: 6px;
  letter-spacing: 0.01em;
}

.card {
  background: var(--color-card-bg);
  border-radius: var(--border-radius-card);
  box-shadow: var(--box-shadow-card);
  padding: var(--card-padding);
  border: 1px solid var(--color-card-border);
}

::-webkit-input-placeholder { color: var(--color-placeholder); opacity: var(--opacity-placeholder);}
::-moz-placeholder { color: var(--color-placeholder); opacity: var(--opacity-placeholder);}
:-ms-input-placeholder { color: var(--color-placeholder); opacity: var(--opacity-placeholder);}
::placeholder { color: var(--color-placeholder); opacity: var(--opacity-placeholder);}

